import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [], // корзина
};

const basketSlice = createSlice({
  name: "@@basket",
  initialState,
  reducers: {
    addToBasket: (state, action) => {
      const dish = action.payload;
      // Проверка, есть ли уже шутка с таким id
      const exists = state.items.some((item) => item.id === dish.id);
      if (exists) {
        // Если есть - ничего не делаем
        return;
      }
      // Добавляем новую шутку
      state.items.push(dish);
      // Обновляем название категории для оповещения
    },
    // Можно добавить другие редукторы, например, для удаления
  },
});

export const { addToBasket } = basketSlice.actions;
export default basketSlice.reducer;
