import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [], // корзина
};

const basketSlice = createSlice({
  name: "@@basket",
  initialState,
  reducers: {
    addToBasket: (state, action) => {
      const item = action.payload;
      state.items.push(item);
    },
    Increment: (state, action) => {
      const item = action.payload;
      state.items = item + 1;
    },
    Decrement: (state, action) => {
      const item = action.payload;
      state.items = item - 1;
    },
  },
});

export const { addToBasket, Increment, Decrement } = basketSlice.actions;
export const basketReducer = basketSlice.reducer;

export const selectBasketItemsCount = (state) => state.basket.items.length;
export const selectBasketItemsIncrement = (state) => state.basket.items;
export const selectBasketItemsDecrement = (state) => state.basket.items;
