import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [], // корзина
};

const basketSlice = createSlice({
  name: "@@basket",
  initialState,
  reducers: {
    addToBasket: (state, action) => {
      const item = action.payload;
      // Проверяем, есть ли уже такой товар по id (или name)
      // const exists = state.items.some((cartItem) => cartItem.id === item.id);
      // if (exists) {
      //   // Товар уже есть, можно либо обновлять количество, либо ничего не делать
      //   return;
      // }
      // Добавляем новый товар
      state.items.push(item);
    },
    // Можно добавить другие редукторы, например, для удаления
  },
});

export const { addToBasket } = basketSlice.actions;
export const basketReducer = basketSlice.reducer;

export const selectBasketItemsCount = (state) => state.basket.items.length;
