import { useDispatch } from "react-redux";
import styled from "styled-components";
import { addToBasket } from "./addToBasket-slice";

const Btn = styled.div`
  display: flex;
  cursor: pointer;
  gap: 8px;
  position: absolute;
  margin-top: -21px;
  margin-left: 61px;
  border: 1px solid rgb(245, 160, 34);
  padding: 10px 38px 2px 38px;
  border-radius: 28px;
  background: white;
`;
const BtnIcon = styled.div`
  color: orange;
`;
const BtnText = styled.div`
  color: black;
  font-weight: var(--fw-bold);
  font-size: 16px;
`;
const BtnQuantity = styled.div``;

export const CardBtn = ({ items }) => {
  const dispatch = useDispatch();

  const handleAddToBasket = () => {
    dispatch(addToBasket(items));
  };

  return (
    // <Btn onClick={handleAddToBasket}>
    //   <BtnIcon>
    //     <i class="material-icons">shopping_cart</i>
    //   </BtnIcon>
    //   <BtnText>Add to Cart</BtnText>
    // </Btn>
    <BtnQuantity></BtnQuantity>
  );
};
