import { useDispatch } from "react-redux";
import styled from "styled-components";
import {
  addToBasket,
  SetAddToBasket,
  SetDecrement,
  SetIncrement,
} from "./addToBasket-slice";

// Стили для кнопки "Добавить в корзину"
const Btn = styled.div`
  display: flex;
  cursor: pointer;
  gap: 8px;
  position: absolute;
  margin-top: -21px;
  margin-left: 61px;
  border: 1px solid rgb(245, 160, 34);
  padding: 10px 38px 2px 38px;
  border-radius: 28px;
  background: white;
`;

// Стили для блока с количеством товара
const BtnQuantityWrapper = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  margin-top: -21px;
  margin-left: 61px;
  border: 1px solid rgb(245, 160, 34);
  padding: 10px 38px 2px 38px;
  border-radius: 28px;
  background: rgb(245, 160, 34);
  gap: 20px;
`;

// const Btn = styled.div`
//   display: flex;
//   cursor: pointer;
//   gap: 8px;
//   position: absolute;
//   margin-top: -21px;
//   margin-left: 61px;
//   border: 1px solid rgb(245, 160, 34);
//   padding: 10px 38px 2px 38px;
//   border-radius: 28px;
//   background: white;
// `;
const BtnIcon = styled.div`
  color: orange;
`;
const BtnText = styled.div`
  color: black;
  font-weight: var(--fw-bold);
  font-size: 16px;
`;
// const BtnQuantity = styled.div`
//   display: flex;
//   gap: 27px;
//   position: absolute;
//   margin-top: -21px;
//   margin-left: 61px;
//   border: 1px solid rgb(245, 160, 34);
//   padding: 10px 38px 2px 38px;
//   border-radius: 28px;
//   background: rgb(245, 160, 34);
// `;
const BtnIncrement = styled.div`
  cursor: pointer;
`;
const BtnList = styled.div``;
const BtnDecrement = styled.div`
  cursor: pointer;
`;

export const CardBtn = ({ items }) => {
  const dispatch = useDispatch();
  // Получаем количество этого товара в корзине
  const quantity = useSelector((state) =>
    selectBasketItemQuantity(state, items.id)
  );

  // Локальный стейт, чтобы определить, добавлен ли товар
  const [isAdded, setIsAdded] = React.useState(!!quantity);

  // Обработчик добавления товара
  const handleAddToBasket = () => {
    dispatch(SetAddToBasket(items));
    setIsAdded(true);
  };

  const handleIncrement = () => {
    dispatch(SetIncrement(items.id));
  };

  const handleDecrement = () => {
    dispatch(SetDecrement(items.id));
  };

  return (
    // <Btn onClick={handleAddToBasket}>
    //   <BtnIcon>
    //     <i class="material-icons">shopping_cart</i>
    //   </BtnIcon>
    //   <BtnText>Add to Cart</BtnText>
    // </Btn>
    <BtnQuantity>
      <BtnDecrement onClick={handleDecrement}>
        <i class="material-icons">remove_circle_outline </i>
      </BtnDecrement>
      <BtnList>1</BtnList>
      <BtnIncrement onClick={handleIncrement}>
        <i class="material-icons">add_circle_outline</i>
      </BtnIncrement>
    </BtnQuantity>
  );
};
